data.examples:
// Example 1: Create a simple text file
const result = await codebolt.fs.createFile(
    'example.txt', 
    'This is the content of the file.', 
    '/home/user/documents'
);
console.log("File created:", result.success);

// Example 2: Create a JavaScript file
const jsContent = `
class TestClass {
    constructor(name) {
        this.name = name;
    }

    testMethod() {
        return 'Hello from ' + this.name;
    }
}
module.exports = { TestClass };
`;

const jsResult = await codebolt.fs.createFile('test-code.js', jsContent, './src');
console.log("JavaScript file created:", jsResult.success);

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "fileName": {
      "type": "string",
      "description": "The name of the file to create.",
      "minLength": 1
    },
    "source": {
      "type": "string",
      "description": "The source content to write into the file."
    },
    "filePath": {
      "type": "string",
      "description": "The path where the file should be created.",
      "minLength": 1
    }
  },
  "required": ["fileName", "source", "filePath"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "createFileResponse"
    },
    "path": {
      "type": "string",
      "description": "The full path where the file was created."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information about the operation."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
