data.examples:
// Example 1: Create a folder in a specific directory
const result = await codebolt.fs.createFolder('exampleFolder', '/home/user/documents');
console.log("Folder created:", result.success);

// Example 2: Create project structure
const projectResult = await codebolt.fs.createFolder('my-project', '.');
if (projectResult.success) {
    await codebolt.fs.createFolder('src', './my-project');
    await codebolt.fs.createFolder('docs', './my-project');
}

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "folderName": {
      "type": "string",
      "description": "The name of the folder to create.",
      "minLength": 1
    },
    "folderPath": {
      "type": "string",
      "description": "The path where the folder should be created.",
      "minLength": 1
    }
  },
  "required": ["folderName", "folderPath"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "createFolderResponse"
    },
    "path": {
      "type": "string",
      "description": "The full path where the folder was created."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
