data.examples:
// Example 1: Delete a file from a specific directory
const result = await codebolt.fs.deleteFile('example.txt', '/home/user/documents');
console.log("File deleted:", result.success);

// Example 2: Delete multiple files
const filesToDelete = [
    { name: 'temp1.txt', path: './temp' },
    { name: 'temp2.txt', path: './temp' }
];

for (const file of filesToDelete) {
    const result = await codebolt.fs.deleteFile(file.name, file.path);
    console.log(`${file.name}: ${result.success ? 'deleted' : 'failed'}`);
}

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "filename": {
      "type": "string",
      "description": "The name of the file to delete.",
      "minLength": 1
    },
    "filePath": {
      "type": "string",
      "description": "The path of the file to delete.",
      "minLength": 1
    }
  },
  "required": ["filename", "filePath"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "deleteFileResponse"
    },
    "path": {
      "type": "string",
      "description": "The full path of the file that was deleted."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
