data.examples:
// Example 1: Delete a folder from a specific directory
const result = await codebolt.fs.deleteFolder('exampleFolder', '/home/user/documents');
console.log("Folder deleted:", result.success);

// Example 2: Cleanup workflow for build artifacts
const buildFolders = ['dist', 'build', 'out', 'temp'];

for (const folderName of buildFolders) {
    const result = await codebolt.fs.deleteFolder(folderName, '.');
    if (result.success) {
        console.log(`âœ… Cleaned up: ${folderName}/`);
    }
}

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "foldername": {
      "type": "string",
      "description": "The name of the folder to delete.",
      "minLength": 1
    },
    "folderpath": {
      "type": "string",
      "description": "The path of the folder to delete.",
      "minLength": 1
    }
  },
  "required": ["foldername", "folderpath"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "deleteFolderResponse"
    },
    "path": {
      "type": "string",
      "description": "The full path of the folder that was deleted."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
