data.examples:
// Get complete application state
const appState = await codebolt.cbstate.getApplicationState();

console.log('Response type:', appState.type); // 'getAppStateResponse'
console.log('Application data:', appState.state);

// Access basic project details
const projectPath = appState.state.projectPath;
const projectName = appState.state.projectName;
const tokenUsed = appState.state.token_used;

console.log(`Project Name: ${projectName}`);
console.log(`Project Path: ${projectPath}`);
console.log(`Tokens Used: ${tokenUsed}`);

// Access custom project settings
const projectState = appState.state.state;
if (projectState.project_name) {
    console.log('Custom Project Name:', projectState.project_name);
}
if (projectState.version) {
    console.log('Project Version:', projectState.version);
}
if (projectState.environment) {
    console.log('Environment:', projectState.environment);
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "getAppStateResponse",
      "description": "Always 'getAppStateResponse'."
    },
    "state": {
      "type": "object",
      "description": "Contains the complete application state.",
      "properties": {
        "token_used": {
          "type": "number",
          "description": "Number of tokens consumed in the current session."
        },
        "chats": {
          "type": "array",
          "description": "Array of chat sessions."
        },
        "projectPath": {
          "type": "string",
          "description": "Full path to the current project directory."
        },
        "projectName": {
          "type": "string",
          "description": "Name of the current project."
        },
        "state": {
          "type": "object",
          "description": "Nested object containing custom project state data.",
          "additionalProperties": true
        }
      }
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information."
    },
    "error": {
      "type": "string",
      "description": "Error details if the operation failed."
    },
    "messageId": {
      "type": "string",
      "description": "A unique identifier for the message."
    },
    "threadId": {
      "type": "string",
      "description": "The thread identifier."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
