data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "getContentResponse",
      "description": "Always 'getContentResponse'."
    },
    "content": {
      "type": "string",
      "description": "The page content."
    },
    "html": {
      "type": "string",
      "description": "The HTML content."
    },
    "text": {
      "type": "string",
      "description": "The text content."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information."
    },
    "error": {
      "type": "string",
      "description": "Error details if the operation failed."
    },
    "messageId": {
      "type": "string",
      "description": "A unique identifier for the message."
    },
    "threadId": {
      "type": "string",
      "description": "The thread identifier."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
