data.examples:
// Navigate to the page
await codebolt.browser.goToPage("https://example.com");

// Wait for page to load
await new Promise(resolve => setTimeout(resolve, 2000));

// Retrieve the HTML content of the current page
const htmlResult = await codebolt.browser.getHTML();
console.log('âœ… HTML retrieved:', {
    success: htmlResult.success,
    htmlLength: htmlResult.html ? htmlResult.html.length : 0
});

// Access the actual HTML content
if (htmlResult.success && htmlResult.html) {
    console.log('HTML content:', htmlResult.html);
    // Process the HTML as needed
} else {
    console.error('Failed to retrieve HTML:', htmlResult.error);
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "htmlReceived",
      "description": "Always 'htmlReceived'."
    },
    "html": {
      "type": "string",
      "description": "The HTML content of the current page."
    },
    "content": {
      "type": "string",
      "description": "Alternative property for the HTML content."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information."
    },
    "error": {
      "type": "string",
      "description": "Error details if the operation failed."
    },
    "messageId": {
      "type": "string",
      "description": "A unique identifier for the message."
    },
    "threadId": {
      "type": "string",
      "description": "The thread identifier."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
