data.examples:
// Wait for connection and create a new page
await codebolt.waitForConnection();
await codebolt.browser.newPage();

// Navigate to a webpage
const goToResult = await codebolt.browser.goToPage('https://example.com');
console.log('✅ Navigated to page:', goToResult);

// Wait for the page to fully load
await new Promise(resolve => setTimeout(resolve, 2000));

// Verify the navigation was successful
const currentUrl = await codebolt.browser.getUrl();
console.log('✅ Current URL after navigation:', currentUrl);

// Example with different websites
await codebolt.browser.goToPage('https://www.google.com');
await codebolt.browser.goToPage('https://github.com');

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "url": {
      "type": "string",
      "description": "The URL to navigate to (must include protocol like https://).",
      "minLength": 1,
      "pattern": "^https?://"
    }
  },
  "required": ["url"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "goToPageResponse",
      "description": "Always 'goToPageResponse'."
    },
    "url": {
      "type": "string",
      "description": "The URL that was navigated to."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the navigation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information."
    },
    "error": {
      "type": "string",
      "description": "Error details if the operation failed."
    },
    "messageId": {
      "type": "string",
      "description": "A unique identifier for the message."
    },
    "threadId": {
      "type": "string",
      "description": "The thread identifier."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
