data.examples:
// Example 1: List files in a specific directory (non-recursive)
const files = await codebolt.fs.listFile('/home/user/documents', false);
console.log('Files in directory:', files);

// Example 2: List files in current directory
const listResult = await codebolt.fs.listFile('.', false);
console.log('âœ… Directory listing:', listResult);

// Example 3: List files recursively
const recursiveResult = await codebolt.fs.listFile('./src', true);
console.log('All files in src directory:', recursiveResult);

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "folderPath": {
      "type": "string",
      "description": "The path of the directory to list files from.",
      "minLength": 1
    },
    "isRecursive": {
      "type": "boolean",
      "description": "Whether to list files recursively."
    },
    "listFiles": {
      "type": "boolean",
      "description": "Optional parameter to use listFiles mode for enhanced listing."
    }
  },
  "required": ["folderPath", "isRecursive"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "fileListResponse"
    },
    "files": {
      "type": "array",
      "description": "An array of file and folder names.",
      "items": {
        "type": "string"
      }
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
