data.examples:
// Example 1: Read a text file
const result = await codebolt.fs.readFile('./example.txt');
console.log("File content:", result.content);

// Example 2: Read a configuration file
const configResult = await codebolt.fs.readFile('./package.json');
if (configResult.success && configResult.content) {
    const config = JSON.parse(configResult.content);
    console.log("Package name:", config.name);
    console.log("Version:", config.version);
}

// Example 3: Read multiple files
const filesToRead = ['./README.md', './package.json', './src/index.js'];

for (const filePath of filesToRead) {
    const result = await codebolt.fs.readFile(filePath);
    if (result.success && result.content) {
        console.log(`${filePath}: ${result.content.length} characters`);
    }
}

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "filePath": {
      "type": "string",
      "description": "The path of the file to read. Can be absolute or relative.",
      "minLength": 1
    }
  },
  "required": ["filePath"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "readFileResponse"
    },
    "content": {
      "type": "string",
      "description": "The content of the file that was read."
    },
    "path": {
      "type": "string",
      "description": "The path of the file that was read."
    },
    "encoding": {
      "type": "string",
      "description": "The encoding used to read the file."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
