data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "screenshotResponse",
      "description": "Always 'screenshotResponse'."
    },
    "payload": {
      "type": "object",
      "description": "Contains the response data including screenshot information.",
      "properties": {
        "screenshot": {
          "type": "string",
          "description": "Base64 encoded image data of the screenshot."
        },
        "fullPage": {
          "type": "boolean",
          "description": "Indicates if it's a full page screenshot."
        },
        "success": {
          "type": "boolean",
          "description": "Indicates if the operation was successful."
        },
        "content": {
          "type": "string",
          "description": "Additional content information."
        },
        "viewport": {
          "type": "object",
          "description": "Viewport information object.",
          "properties": {
            "width": {
              "type": "number",
              "description": "Viewport width in pixels."
            },
            "height": {
              "type": "number",
              "description": "Viewport height in pixels."
            }
          }
        }
      }
    },
    "eventId": {
      "type": "string",
      "description": "Event identifier for the screenshot action."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information."
    },
    "error": {
      "type": "string",
      "description": "Error details if the operation failed."
    },
    "messageId": {
      "type": "string",
      "description": "A unique identifier for the message."
    },
    "threadId": {
      "type": "string",
      "description": "The thread identifier."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
