data.examples:
// Example 1: Search for files matching a regex pattern
const result = await codebolt.fs.searchFiles('/home/user/documents', 'example', '*.txt');
console.log('Search results:', result);

// Example 2: Search for JavaScript files containing "function"
const jsSearch = await codebolt.fs.searchFiles('.', 'function', '*.js');
console.log('JavaScript files with functions:', jsSearch);

// Example 3: Search for configuration files
const configSearch = await codebolt.fs.searchFiles('.', 'config', '*.json');
console.log('Configuration files:', configSearch);

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "path": {
      "type": "string",
      "description": "The path to search within.",
      "minLength": 1
    },
    "regex": {
      "type": "string",
      "description": "The regex pattern to search for.",
      "minLength": 1
    },
    "filePattern": {
      "type": "string",
      "description": "The file pattern to match files.",
      "minLength": 1
    }
  },
  "required": ["path", "regex", "filePattern"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "searchFilesResponse"
    },
    "results": {
      "type": "array",
      "description": "An array of matching file paths.",
      "items": {
        "type": "string"
      }
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
