data.examples:
// Example 1: Send a simple text message
codebolt.chat.sendMessage("Hello, CodeBolt!");

// Example 2: Send a message with payload data
codebolt.chat.sendMessage("File processing complete", {
    timestamp: new Date().toISOString(),
    source: 'codeboltjs-script',
    fileCount: 5,
    status: 'success'
});

// Example 3: Send status update
codebolt.chat.sendMessage("Processing files...", {
    progress: 75,
    currentFile: "index.js",
    totalFiles: 10
});

// Example 4: Send error notification
codebolt.chat.sendMessage("Error occurred during operation", {
    errorCode: "ERR_001",
    errorMessage: "File not found",
    timestamp: new Date().toISOString()
});

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "message": {
      "type": "string",
      "description": "The message to be sent.",
      "minLength": 1
    },
    "payload": {
      "description": "Additional data to send along with the message."
    }
  },
  "required": ["message"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "null",
  "description": "This method returns void and does not provide a response."
}
