data.examples:
// Example 1: Update a file with new content
const result = await codebolt.fs.updateFile(
    'example.txt', 
    '/home/user/documents', 
    'This is the updated content.'
);
console.log("File updated:", result.success);

// Example 2: Update a configuration file
const configContent = JSON.stringify({
    name: "my-app",
    version: "2.0.0",
    description: "Updated application configuration"
}, null, 2);

const configResult = await codebolt.fs.updateFile('package.json', '.', configContent);
console.log("Config updated:", configResult.success);

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "filename": {
      "type": "string",
      "description": "The name of the file to update.",
      "minLength": 1
    },
    "filePath": {
      "type": "string",
      "description": "The path of the file to update.",
      "minLength": 1
    },
    "newContent": {
      "type": "string",
      "description": "The new content to write into the file, replacing the existing content."
    }
  },
  "required": ["filename", "filePath", "newContent"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "updateFileResponse"
    },
    "path": {
      "type": "string",
      "description": "The full path of the file that was updated."
    },
    "bytesWritten": {
      "type": "number",
      "description": "The number of bytes written to the file."
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
