data.examples:
// Example 1: Write content to a file
const result = await codebolt.fs.writeToFile(
    '/home/user/documents/example.txt', 
    'This is the new content.'
);
console.log('Write result:', result);

// Example 2: Write to file in current directory
const writeResult = await codebolt.fs.writeToFile(
    './fs-test-file.txt',
    'This is content written using writeToFile method'
);
console.log('âœ… Write to file:', writeResult);

data.input_json_schema:
{
  "type": "object",
  "properties": {
    "relPath": {
      "type": "string",
      "description": "The relative path of the file to write to.",
      "minLength": 1
    },
    "newContent": {
      "type": "string",
      "description": "The new content to write into the file."
    }
  },
  "required": ["relPath", "newContent"],
  "additionalProperties": false
}

data.output_json_schema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "const": "writeToFileResponse"
    },
    "success": {
      "type": "boolean",
      "description": "Indicates if the operation was successful."
    },
    "message": {
      "type": "string",
      "description": "A message with additional information about the operation."
    }
  },
  "required": ["type"],
  "additionalProperties": false
}
